import React from "react";
import './ColorButtons.css'

const ColorButton = ({ color, addCircle }) => {
  return (
    <button
      onClick={() => addCircle(color)}
      className="ColorButton"
      style={{ backgroundColor: color }}
    >
      {color}
    </button>

  )
}

export default ColorButton;
